void tcl_split_list(QString &str, QStringList &list) {
  int argc;
  const char **argv;

  list.clear();
  if (str.left(1)=="{" && str.right(1)=="}") {
    str=str.mid(1,str.length()-2);
  } else if (str.left(1)=="\"" && str.right(1)=="\"") {
    str=str.mid(1,str.length()-2);
  }
  if (Tcl_SplitList(str.ascii(),&argc,&argv) != TCL_OK) {
    list.append(str);
  } else {
    for (int i = 0; i < argc; i++) {
      list.append(argv[i]);
    }
    ckfree((char *) argv);
  }
}

